package ru.nsu.tumilevich;import org.junit.jupiter.api.Test;import static org.junit.jupiter.api.Assertions.assertArrayEquals;import static ru.nsu.tumilevich.Sort.heapsort;@SuppressWarnings("checkstyle:Indentation")class SortTest {	@Test	void checkZero() {		Integer[] task = {};		Integer[] answer = heapsort(task);		Integer[] expected = {};		assertArrayEquals(answer, expected);	}	@Test	void checkOne() {		Integer[] task = {0};		Integer[] answer = heapsort(task);		Integer[] expected = {0};		assertArrayEquals(answer, expected);	}	@Test	void checkN() {		Integer[] task = {7, 6, 2, 5, 8, 3, 1, 4};		Integer[] answer = heapsort(task);		Integer[] expected = {1, 2, 3, 4, 5, 6, 7, 8};		assertArrayEquals(answer, expected);	}	@Test	void checkN_of_Neg() {		Integer[] task = {-6, -3, -8, -1};		Integer[] answer = heapsort(task);		Integer[] expected = {-8, -6, -3, -1};		assertArrayEquals(answer, expected);	}	@Test	void checkN_with_Neg() {		Integer[] task = {7, -6, 2, 5, 8, 3, 1, -4};		Integer[] answer = heapsort(task);		Integer[] expected = {-6, -4, 1, 2, 3, 5, 7, 8};		assertArrayEquals(answer, expected);	}	@Test	void checkN_with_Neg_and_Zero() {		Integer[] task = {7, -6, 2, 5, 0, 8, 3, 1, -4};		Integer[] answer = heapsort(task);		Integer[] expected = {-6, -4, 0, 1, 2, 3, 5, 7, 8};		assertArrayEquals(answer, expected);	}}