package ru.nsu.tumilevich;import org.junit.jupiter.api.AfterEach;import org.junit.jupiter.api.BeforeEach;import org.junit.jupiter.api.Test;import java.io.ByteArrayInputStream;import java.io.ByteArrayOutputStream;import java.io.InputStream;import java.io.PrintStream;import java.util.Scanner;import static org.junit.jupiter.api.Assertions.assertTrue;class BlackjackMainTest {	private final InputStream originalSystemIn = System.in;	private final PrintStream originalSystemOut = System.out;	private ByteArrayOutputStream testOutput;	@BeforeEach	void setUp() {		testOutput = new ByteArrayOutputStream();		System.setOut(new PrintStream(testOutput));	}	@AfterEach	void tearDown() {		System.setIn(originalSystemIn);		System.setOut(originalSystemOut);	}	private void provideInput(String data) {		ByteArrayInputStream testIn = new ByteArrayInputStream(data.getBytes());		System.setIn(testIn);	}	private String getOutput() {		return testOutput.toString().replaceAll("\\r\\n", "\n");	}	@Test	void testMainSingleRoundPlayerName() {		String input = "Игрок\nПас\nНет\n";		provideInput(input);		Scanner originalScanner = BlackjackMain.scanner;		try {			BlackjackMain.scanner = new Scanner(System.in);			BlackjackMain.main(new String[]{});		} finally {			BlackjackMain.scanner = originalScanner;		}		String output = getOutput();		assertTrue(output.contains("Добро пожаловать в Блэкджек!"));		assertTrue(output.contains("Раунд 1"));	}	@Test	void testMainMultipleRounds() {		String input = "Игрок\nПас\nПас\nНет\n";		provideInput(input);		Scanner originalScanner = BlackjackMain.scanner;		try {			BlackjackMain.scanner = new Scanner(System.in);			BlackjackMain.main(new String[]{});		} finally {			BlackjackMain.scanner = originalScanner;		}		String output = getOutput();		assertTrue(output.contains("Раунд 1"));		assertTrue(output.contains("Раунд 2"));		assertTrue(output.contains("Продолжаем?"));	}	@Test	void testRoundPlayerBusts() {		String input = "Игрок\nВзять\nВзять\nВзять\nВзять\nВзять\nНет\n";		provideInput(input);		Scanner originalScanner = BlackjackMain.scanner;		try {			BlackjackMain.scanner = new Scanner(System.in);			BlackjackMain.main(new String[]{});		} finally {			BlackjackMain.scanner = originalScanner;		}		String output = getOutput();		assertTrue(output.contains("перебор") || output.contains("Перебор"));	}}